{attribute 'enable_dynamic_creation'}
FUNCTION_BLOCK FBComputeGamma
VAR_INPUT
    iAlpha : INT;
    iBeta : INT;
END_VAR
VAR_OUTPUT
    iGamma : INT;
END_VAR

PROGRAM PLC_PRG
VAR
    pComputeGamma : POINTER TO FBComputeGamma; // Typed pointer
    xInit : BOOL := TRUE;
    xDelete : BOOL;
    iResult : INT;
END_VAR

IF (xInit) THEN
    pComputeGamma := NEW(FBComputeGamma); // Allocates memory
    xInit := FALSE;
END_IF
pComputeGamma^.iAlpha := (pComputeGamma^.iAlpha + 1) MOD 100;
pComputeGamma^.iBeta := 10;
pComputeGamma^(); // Calls the FB
iResult := pComputeGamma^.iGamma;
IF (xDelete) THEN
    DELETE(pComputeGamma); // Frees memory
END_IF

